      merklized: inputs[0],
      userID: inputs[1],
      circuitQueryHash: inputs[2],
      issuerState: inputs[3],
      requestID: inputs[4],
      challenge: inputs[5],
      gistRoot: inputs[6],
      issuerID: inputs[7],
      isRevocationChecked: inputs[8],
      issuerClaimNonRevState: inputs[9],
      timestamp: inputs[10]
